<?php get_header(); ?><div class="grid-container">	<div class="grid-x grid-margin-x">		<div class="cell auto">						<?php if( have_posts() ) : while ( have_posts() ) : the_post(); ?>							<?php the_title('<h1 class="entry-title">','</h1>'); ?>							<div class="entry-content">										<?php the_content(); ?>									</div>							<?php if( is_single()) { ?>								<div class="callout small auction-info">					<div class="entry-content auction date">						<h4>Auction Ends</h4> 						<p><small><?php the_field('auction_date'); ?></small></p>					</div> 					<hr />					<div class="entry-content preview-description">						<h4>Auction Preview</h4>						<small><?php the_field('auction_preview'); ?></small>					</div>					<hr />					<div class="entry-content pickup-description">					<h4>Auction Pickup</h4>						<small><?php the_field('auction_pickup'); ?></small>					</div>				</div>				<?php } elseif( is_search() || is_404() ) { ?>									<h3>Search results for:<small> <?php echo get_search_query(); ?></small></h3>						<div class="callout">						<h4>Can't find what you're looking for?</h4>						<?php get_search_form(); ?>						</div>							<?php } ?>						<?php endwhile; endif; // loop ?>					</div>				<?php get_sidebar(); ?>			</div></div><?php get_footer(); ?>